"use client";

import React, { useEffect } from "react";

const items = [
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",

  // "https://via.placeholder.com/800x500",
  // "https://via.placeholder.com/800x500",
  // "https://via.placeholder.com/800x500",
  // "https://via.placeholder.com/800x500",
  // "https://via.placeholder.com/800x500",
  // "https://via.placeholder.com/800x500",
];

const HeroSecondSite = () => {
  useEffect(() => {
    const style = document.createElement("style");
    style.innerHTML = keyframes;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <section style={styles.wrapper}>
      <div style={styles.scene}>
        <div style={styles.carousel}>
          {items.map((src, index) => {
            const angle = (360 / items.length) * index;
            return (
              <div
                key={index}
                style={{
                  ...styles.card,
                  transform: `rotateY(${angle}deg) translateZ(620px)`,
                }}
              >
                <img src={src} alt="" style={styles.image} />
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

/* ================= STYLES ================= */

const styles: Record<string, React.CSSProperties> = {
  wrapper: {
    width: "100vw",
    height: "100vh",
    background: "#000",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
  },

  scene: {
    width: "100%",
    height: "100%",
    // perspective: "1600px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },

  carousel: {
    width: "100%",
    height: 420,
    position: "relative",
    transformStyle: "preserve-3d",
    animation: "slowSpin 120s linear infinite", //   
  },

  card: {
    position: "absolute",
    left: "50%",
    top: "50%",
    width: 420,
    height: 260,
    marginLeft: -210,
    marginTop: -130,
    borderRadius: 12,
    overflow: "hidden",
    boxShadow: "0 30px 60px rgba(0,0,0,0.6)",
    background: "#111",
  },

  image: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },
};

/* ================= KEYFRAMES ================= */

const keyframes = `
@keyframes slowSpin {
  from {
    transform: rotateY(0deg);
  }
  to {
    transform: rotateY(360deg);
  }
}
`;

export default HeroSecondSite;
